import React, { useState } from "react";

function Temp1() {
  //   // State to manage the width of the div
  //   const [divWidth, setDivWidth] = useState(200); // Initial width

  //   // Function to handle changing the width
  //   const handleChangeWidth = () => {
  //     // Update the width to a new value
  //     const newWidth = Math.floor(Math.random() * 300) + 100; // Generate a random width between 100 and 400
  //     setDivWidth(newWidth);
  //   };

  //   return (
  //     <div>
  //       {/* Render the div with dynamic width */}
  //       <div
  //         style={{
  //           width: `${divWidth}px`,
  //           height: "100px",
  //           backgroundColor: "lightblue",
  //         }}
  //       >
  //         Dynamic Div
  //       </div>

  //       {/* Button to change the width */}
  //       <button onClick={handleChangeWidth}>Change Width</button>
  //     </div>
  //   );
  // }

  const [isDragging, setIsDragging] = useState(false);
  const [progressLevel, setProgressLevel] = useState(0);
  const [emoji, setEmoji] = useState("üòê");

  const handleMouseDown = () => {
    setIsDragging(true);
    document.addEventListener("mousemove", handleDrag);
    document.addEventListener("mouseup", handleMouseUp);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    document.removeEventListener("mousemove", handleDrag);
  };

  const handleDrag = (e) => {
    if (isDragging) {
      const progressBar = document.getElementById("progress-bar");
      const { left, width } = progressBar.getBoundingClientRect();
      let newX = e.clientX - left;
      newX = Math.max(0, Math.min(newX, width));
      const newProgressLevel = Math.floor((newX / width) * 5);
      setProgressLevel(newProgressLevel);
      updateEmoji(newProgressLevel);
    }
  };

  const updateEmoji = (level) => {
    switch (level) {
      case 0:
        setEmoji("üò°");
        break;
      case 1:
        setEmoji("üòü");
        break;
      case 2:
        setEmoji("üòä");
        break;
      case 3:
        setEmoji("üòÄ");
        break;
      case 4:
        setEmoji("üòÉ");
        break;
      case 5:
        setEmoji("üòÅ");
        break;
      default:
        setEmoji("üòê");
    }
  };

  return (
    <div className="App">
      <div id="progress-bar-container">
        <div id="progress-bar">
          <div id="toast">
            <div
              id="draggable-handle"
              onMouseDown={handleMouseDown}
              onMouseUp={handleMouseUp}
            >
              <span id="emoji">{emoji}</span>
            </div>
          </div>
        </div>
        <div id="rating-container">
          <p id="rating-text">Rating is shown here...</p>
        </div>
      </div>
    </div>
  );
}

export default Temp1;
