import React, { useState } from "react";
// import "./App.css"; // You can create a CSS file for styling

const ProgressBar = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [progressLevel, setProgressLevel] = useState(0);

  const handleDragStart = () => {
    setIsDragging(true);
  };

  const handleDragEnd = () => {
    setIsDragging(false);
  };

  const handleDrag = (e) => {
    if (isDragging) {
      const progressBar = document.getElementById("progress-bar");
      const { left, width } = progressBar.getBoundingClientRect();
      let newX = e.clientX - left;

      // Ensure the handle stays within the bounds of the progress bar
      newX = Math.max(0, Math.min(newX, width));

      // Calculate the progress level (0 to 5)
      const newProgressLevel = Math.ceil((newX / width) * 5);

      setProgressLevel(newProgressLevel);
    }
  };

  const updateEmoji = (level) => {
    switch (level) {
      case 0:
        return "üò°";
      case 1:
        return "üòü";
      case 2:
      case 3:
        return "üòê";
      case 4:
        return "üòä";
      case 5:
        return "üòÅ";
      default:
        return "üòê";
    }
  };

  return (
    <div id="progress-bar-container">
      <div
        id="progress-bar"
        onMouseDown={handleDragStart}
        onMouseUp={handleDragEnd}
        onMouseMove={handleDrag}
      >
        <div
          id="draggable-handle"
          style={{ left: `${(progressLevel / 5) * 100}%` }}
        ></div>
      </div>
      <div id="emoji-container">
        <span id="emoji" style={{ fontSize: "24px" }}>
          {updateEmoji(progressLevel)}
        </span>
      </div>
    </div>
  );
};

export default ProgressBar;
