import React, { useState, useRef } from "react";
import "./P"; // Make sure to have appropriate styles for the progress bar in ProgressBar.css

const ProgressBar = () => {
  const [ptrPosition, setPtrPosition] = useState(0);
  const [level, setLevel] = useState(0);
  const ptrRef = useRef(null);
  let isMouseDown = false;

  const levels = [
    "😠", // Angry Emoji
    "😐", // Not So Happy Emoji
    "😊", // Happy Emoji
    "😁", // Very Happy Emoji
    "😍", // Excellent Emoji
  ];

  const calculateLevel = () => {
    const percentage = (ptrPosition / ptrRef.current.clientWidth) * 100;
    const calculatedLevel = Math.ceil(percentage / (100 / levels.length));
    return calculatedLevel;
  };

  const handleMouseDown = (e) => {
    isMouseDown = true;
  };

  const handleMouseMove = (e) => {
    if (isMouseDown) {
      const rect = ptrRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      setPtrPosition(x);

      const calculatedLevel = calculateLevel();
      setLevel(calculatedLevel);
    }
  };

  const handleMouseUp = () => {
    isMouseDown = false;
  };

  return (
    <div
      className="progress-wrapper"
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      ref={ptrRef}
    >
      <div className="progress" style={{ width: `${ptrPosition}px` }}>
        <div className="pointer">{levels[level]}</div>
      </div>
    </div>
  );
};

export default ProgressBar;
