import React, { useState } from "react";
import "./Temp.css"; // Import your CSS file

function App() {
  const [isDragging, setIsDragging] = useState(false);
  const [progressLevel, setProgressLevel] = useState(0);
  const [toastWidth, setToastWidth] = useState(0);

  const handleMouseDown = (e) => {
    e.preventDefault();
    setIsDragging(true);
    document.addEventListener("mousemove", handleDrag);
    document.addEventListener("mouseup", handleMouseUp);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    document.removeEventListener("mousemove", handleDrag);

    // Add the burst-emoji class to trigger the burst animation
    document.getElementById("emoji").classList.add("burst-emoji");

    // Reset the burst animation after a delay
    setTimeout(() => {
      document.getElementById("emoji").classList.remove("burst-emoji");
    }, 500);
  };

  const handleDrag = (e) => {
    if (isDragging) {
      const progressBar = document.getElementById("progress-bar");
      const { left, width } = progressBar.getBoundingClientRect();
      let newX = e.clientX - left;
      newX = Math.max(0, Math.min(newX, width));
      const newProgressLevel = Math.floor((newX / width) * 5);
      setProgressLevel(newProgressLevel);
    }
  };

  const updateEmoji = (level) => {
    const emojiSpan = document.getElementById("emoji");
    switch (level) {
      case 0:
        emojiSpan.textContent = "üò°";
        break;
      case 1:
        emojiSpan.textContent = "üòü";
        break;
      case 2:
        emojiSpan.textContent = "üòä";
        break;
      case 3:
        emojiSpan.textContent = "üòÄ";
        break;
      case 4:
        emojiSpan.textContent = "üòÉ";
        break;
      case 5:
        emojiSpan.textContent = "üòÅ";
        break;
      default:
        emojiSpan.textContent = "üòê";
    }
  };

  return (
    <div className="App">
      <div id="progress-bar-container">
        <div id="progress-bar">
          <div
            id="toast"
            style={{
              width: `${toastWidth}`,
            }}
          >
            <div
              id="draggable-handle"
              onMouseDown={(() =>{
                handleMouseDownhandleMouseDown(this);
              })}
              onMouseUp={handleMouseUp}
            >
              <span id="emoji">üòê</span>
            </div>
          </div>
        </div>
        <div id="rating-container">
          <p id="rating-text">Rating is shown here...</p>
        </div>
      </div>
    </div>
  );
}

export default App;
