import React, { useState } from "react";

export default function HookUseEffect1() {
  console.log("hello");
  const [count, setCount] = useState(() => 0);
  const [value, setValue] = useState(() => "None");

  const handleInc = () => {
    setCount((prevValue) => {
      return prevValue + 1;
    });
  };
  const handleDec = () => {
    setCount((prevValue) => {
      return prevValue - 1;
    });
  };
  return (
    <>
      <h1>{count}</h1>


      <button onClick={handleInc}>Increment</button>
      <button onClick={handleDec}>Decrement</button>

      <button onClick={handleDec}>First</button>
      <button onClick={handleDec}>Second</button>
      <button onClick={handleDec}>Third</button>
    </>
  );
}
