import React, { useState, useRef, useEffect } from "react";
import "./ProgressBar.css"; // Import your styles

const ProgressBar = () => {
  const [ptrPosition, setPtrPosition] = useState(0);
  const [emoji, setEmoji] = useState("ðŸ˜ ");
  const ptrRef = useRef(null);
  const isMouseDownRef = useRef(false);

  const levels = [
    "ðŸ˜ ", // Angry Emoji
    "ðŸ˜", // Not So Happy Emoji
    "ðŸ˜Š", // Happy Emoji
    "ðŸ˜", // Very Happy Emoji
    "ðŸ˜", // Excellent Emoji
  ];

  const calculateLevel = () => {
    const percentage = (ptrPosition / ptrRef.current.clientWidth) * 100;
    const calculatedLevel = Math.min(
      levels.length - 1,
      Math.floor(percentage / (100 / levels.length))
    );
    return levels[calculatedLevel];
  };

  const handleMouseDown = () => {
    isMouseDownRef.current = true;
  };

  const handleMouseMove = (e) => {
    if (isMouseDownRef.current) {
      const rect = ptrRef.current.getBoundingClientRect();
      let x = e.clientX - rect.left;

      // Ensure x stays within the bounds of the progress bar
      x = Math.max(0, Math.min(x, ptrRef.current.clientWidth));

      requestAnimationFrame(() => {
        setPtrPosition(x);
        const calculatedEmoji = calculateLevel();
        setEmoji(calculatedEmoji);
      });
    }
  };

  const handleMouseUp = () => {
    isMouseDownRef.current = false;
  };

  useEffect(() => {
    const handleMouseLeave = () => {
      isMouseDownRef.current = false;
    };

    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
    document.addEventListener("mouseleave", handleMouseLeave);

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
      document.removeEventListener("mouseleave", handleMouseLeave);
    };
  }, []);

  return (
    <div
      className="progress-wrapper"
      onMouseDown={handleMouseDown}
      ref={ptrRef}
    >
      <div className="progress" style={{ width: `${ptrPosition}px` }}>
        <div className="pointer">{emoji}</div>
      </div>
    </div>
  );
};

export default ProgressBar;
