        inputRef.current.focus();
    }

    const inputRef = useRef();
    return (
        <>

            <input ref={inputRef} type="number" value={count} />
            <p onClick={handleOnClick}>This is the text to click on .</p>
            <button >Click me</button>

        </>
    )
}
