          </div>
          <div className="answer-section">
            {questions[index].answerOptions.map((val, key) => {
              return (
                <button
                  isTrue={val.isCorrect}
                  key={key}
                  name={key}
                  onClick={() => {
                    handleClick(val.isCorrect);
                  }}
                >
                  {val.answerText}
                </button>
              );
            })}
          </div>
        </>
      )}
    </div>
  );
};

export default Quiz;
