import React, { useState } from "react";

export default function UseState() {
  const [count, setCount] = useState(0);

  const increment = () => {
    // setCount((prevValue) => {
    //   return prevValue + 1;
    // });
    setCount(count + 1 ) ;
    setCount(count + 1 ) ;
};
const decrement = () => {
    setCount(count - 1 ) ;
};


  return (
    <>
      <div>
        <div>{count}</div>
        <button onClick={increment}>Incre</button>
        <button onClick={decrement}>Decr</button>
      </div>
    </>
  );
}
